const ACCENT_COLORS = [
  "#6c4cf5",
  "#b44bff",
  "#f3722c",
  "#43aa8b",
  "#f94144",
  "#577590",
  "#f8961e",
  "#6f2dbd",
  "#90be6d"
];

const DISNEY_API_BASE = "https://api.disneyapi.dev/character";

const API_ALIASES = {
  "Mickey Mouse": "Mickey Mouse",
  "Minnie Mouse": "Minnie Mouse",
  Donald: "Donald Duck",
  Goofy: "Goofy",
  Pluto: "Pluto",
  Ariel: "Princess Ariel",
  Anna: "Princess Anna",
  Moana: "Moana",
  Simba: "Simba",
  Stitch: "Stitch",
  Mulan: "Fa Mulan",
  Rapunzel: "Rapunzel",
  Hercules: "Hercules",
  Bella: "Belle",
  Aladdin: "Aladdin",
  Olaf: "Olaf",
  "Buzz Lightyear": "Buzz Lightyear",
  Jengibre: null,
  Tiana: "Princess Tiana",
  Cenicienta: "Cinderella",
  Blancanieves: "Snow White",
  Jasmine: "Princess Jasmine",
  Pocahontas: "Pocahontas",
  Merida: "Merida"
};

const clamp = (value, min, max) => Math.min(Math.max(value, min), max);

const lightenHex = (hex, amount = 0.5) => {
  const normalized = hex.replace("#", "");
  if (normalized.length !== 6) {
    return hex;
  }

  const numeric = parseInt(normalized, 16);
  const r = (numeric >> 16) & 0xff;
  const g = (numeric >> 8) & 0xff;
  const b = numeric & 0xff;
  const ratio = clamp(amount, 0, 1);
  const toHex = (component) => component.toString(16).padStart(2, "0");

  const newR = Math.round(r + (255 - r) * ratio);
  const newG = Math.round(g + (255 - g) * ratio);
  const newB = Math.round(b + (255 - b) * ratio);

  return `#${toHex(newR)}${toHex(newG)}${toHex(newB)}`;
};

const CHARACTERS = BASE_CHARACTERS.map((character, index) => {
  const accent = ACCENT_COLORS[index % ACCENT_COLORS.length];
  return {
    ...character,
    accent,
    accentSoft: lightenHex(accent, 0.55)
  };
});

const CharacterCard = ({ character, loading }) => {
  if (!character) {
    return (
      <article className="card card--empty">
        <p>
          Aun no hay carta generada. Pulsa el boton para conocer al personaje
          destacado del dia.
        </p>
      </article>
    );
  }

  return (
    <article className="card" style={{ "--accent": character.accent, "--accent-soft": character.accentSoft }}>
      <div className="card__glow" />
      <div className="card__sparkles">
        <span />
        <span />
        <span />
        <span />
      </div>
      <header className="card__header">
        <span className="card__badge">Personaje destacado</span>
        <h2>{character.name}</h2>
        <p className="card__subtitle">{character.title}</p>
      </header>

      <section className="card__body">
        <div className="card__media">
          {character.imageUrl ? (
            <img src={character.imageUrl} alt={character.name} loading="lazy" />
          ) : (
            <div className="card__media-placeholder">
              <span>{character.name.charAt(0).toUpperCase()}</span>
              <p>Imagen no disponible</p>
            </div>
          )}
          {loading && (
            <div className="card__media-loader" aria-hidden="true">
              <span />
            </div>
          )}
        </div>
        <p>{character.description}</p>
      </section>

      <footer className="card__footer">
        <ul className="card__keywords">
          {character.keywords.map((keyword) => (
            <li key={keyword}>{keyword}</li>
          ))}
        </ul>
      </footer>
    </article>
  );
};

function App() {
  const [selectedCharacter, setSelectedCharacter] = useState(null);
  const [history, setHistory] = useState([]);
  const [isRevealing, setIsRevealing] = useState(false);
  const [isLoadingImage, setIsLoadingImage] = useState(false);
  const imageCache = useRef(new Map());

  const fetchCharacterImage = async (character) => {
    const queryName = API_ALIASES[character.name] ?? character.name;

    if (!queryName) {
      return character.image ?? null;
    }

    const response = await fetch(
      `${DISNEY_API_BASE}?name=${encodeURIComponent(queryName)}`
    );

    if (!response.ok) {
      throw new Error("Failed to fetch character image");
    }

    const payload = await response.json();
    const results = Array.isArray(payload.data) ? payload.data : [];

    const normalizedQuery = queryName.toLowerCase();
    const exactMatch = results.find((entry) => {
      const entryName = (entry.name ?? "").toLowerCase();
      return entry.imageUrl && entryName.includes(normalizedQuery);
    });

    const fallback = results.find((entry) => entry.imageUrl);
    return exactMatch?.imageUrl ?? fallback?.imageUrl ?? character.image ?? null;
  };

  const handleReveal = async () => {
    if (isRevealing) return;

    setIsRevealing(true);

    const previousName = selectedCharacter?.name;
    const pool = CHARACTERS.filter((character) => character.name !== previousName);
    const candidates = pool.length > 0 ? pool : CHARACTERS;
    const next =
      candidates[Math.floor(Math.random() * candidates.length)];

    setHistory((prev) =>
      [next.name, ...prev.filter((name) => name !== next.name)].slice(0, 6)
    );

    const hasStaticImage =
      typeof next.image === "string" && next.image.trim().length > 0;

    if (hasStaticImage) {
      const sanitizedImage = next.image.trim();
      imageCache.current.set(next.name, sanitizedImage);
      setSelectedCharacter({ ...next, imageUrl: sanitizedImage });
      setIsLoadingImage(false);
      setIsRevealing(false);
      return;
    }

    if (imageCache.current.has(next.name)) {
      const cachedImage = imageCache.current.get(next.name);
      setSelectedCharacter({
        ...next,
        imageUrl: cachedImage ?? null
      });
      setIsLoadingImage(false);
      setIsRevealing(false);
      return;
    }

    setIsLoadingImage(true);
    setSelectedCharacter({ ...next, imageUrl: next.image ?? null });

    try {
      const imageUrl = await fetchCharacterImage(next);

      const resolvedImage = imageUrl ?? next.image ?? null;
      imageCache.current.set(next.name, resolvedImage);

      setSelectedCharacter((current) =>
        current && current.name === next.name
          ? { ...next, imageUrl: resolvedImage }
          : current
      );
    } catch (error) {
      setSelectedCharacter((current) =>
        current && current.name === next.name
          ? { ...next, imageUrl: next.image ?? null }
          : current
      );
    } finally {
      setIsLoadingImage(false);
      setIsRevealing(false);
    }
  };

  return (
    <div className="app">
      <main className="app__layout">
        <section className="intro">
          <h1>Cartas de presentacion Disney</h1>
          <p>
            Presiona el boton para generar una carta unica que destaca las
            fortalezas de un personaje clasico de Disney.
          </p>
        </section>

        <section className="actions">
          <button
            type="button"
            className="app__reveal-button"
            onClick={handleReveal}
            disabled={isRevealing}
          >
            {isRevealing
              ? "Buscando magia..."
              : selectedCharacter
              ? "Generar otro personaje"
              : "Revelar personaje"}
          </button>
        </section>

        <section className="card-slot">
          <CharacterCard character={selectedCharacter} loading={isLoadingImage} />
        </section>

        {history.length > 0 && (
          <section className="app__history">
            <h3>Personajes recientes</h3>
            <ul>
              {history.map((name) => (
                <li key={name}>{name}</li>
              ))}
            </ul>
          </section>
        )}
      </main>
    </div>
  );
}

export default App;
